package com.zhianjia.m.zhianjia.haier.dialog;import android.app.Dialog;import android.content.Context;import android.widget.TextView;import com.zhianjia.m.zhianjia.R;public class SmartConfigDialog extends Dialog {	private Context mContext;	private TextView button;	public SmartConfigDialog(Context context) {		super(context, R.style.notitle_dialog);		mContext = context;		this.setContentView(R.layout.smartconfig_dialog);		button = (TextView) findViewById(R.id.btn_cancel_text);	}	public void setOnCancelListener(			android.view.View.OnClickListener listener) {		button.setOnClickListener(listener);	}	public SmartConfigDialog(Context context, int resLayoutId) {		super(context, resLayoutId);		mContext = context;		this.setContentView(resLayoutId);		button = (TextView) findViewById(R.id.btn_cancel_text);	}}